#include<stdio.h>
#include<stdlib.h>
#include<conio.h>
#include<math.h>
#include<string.h>
#define tam 50
int menu ();void cargavector(int,char,float,int*,char*,float*,int);void informedia(int*,char*,float*,int);int main(){int codart,vcodart[tam]={0},i=0,opcion=0;char tipo, vtipo[tam]={ 'z' };float imp,vimp[tam]={0};opcion=menu();while(opcion!=3){switch(opcion){case 1:system("cls");printf("\n-----REGISTRO DE VENTAS-----\n");printf("\n\nINGRESAR CODIGO DE ARTICULO : ");fflush(stdin);scanf("%i",&codart);printf("\nINGRESAR TIPO DE ARTICULO \n's' = soladora \n't' = taladro \n 'h' = hidrolavadora \n\nOPCION SELECCIONADA : ");fflush(stdin);scanf("%c",&tipo);printf("\nINGRESE  EL IMPORTE DE LA COMPRA : \n\n");fflush(stdin);scanf("%f",&imp);cargavector(codart,tipo,imp,vcodart,vtipo,vimp,i);i++;system("pause\n");system("cls");break;case 2:informedia(vcodart,vtipo,vimp,i);break;default:printf("\nOPCION INCORRECTA");break;}opcion=menu();}}int menu(){int inicio;printf("\n-----MENU PRINCIPAL-----\n");printf("\n1-INGRESAR DATOS DE LA VENTA\n");printf("\n2-GENERAR INFORMES DEL DIA\n");printf("\n3-SALIR\n");printf("\nOPCION SELECCIONADA: ");scanf("%i",&inicio);fflush(stdin);return inicio;}void cargavector(int x,char y,float z,int *vcodart,char *vtipo,float *vimp,int j){*(vcodart + j) = x;*(vtipo +j) = y;*(vimp + j) = z;}void informedia(int *vcodart,char *vtipo,float *vimp,int i){int cs=0,ct=0,ch=0,j;float totalfacturado=0,recaudadotaladro=0;for(j=0;j<i;j++){if(*(vtipo+j)=='s') cs++;else if(*(vtipo+j)=='t'){ct++;recaudadotaladro=recaudadotaladro + *(vimp + j);}else if(*(vtipo+j)=='h') ch++;totalfacturado=totalfacturado + *(vimp + j);}system("cls");printf("\n-----INFORME DE VENTAS DEL DIA-----\n");printf("\nCANTIDAD DE CLIENTES QUE HICIERON LA COMPRA DE UNA SOLDADORA : %i",cs);printf("\nCANTIDAD DE CLIENTES QUE HICIERON LA COMPRA DE UN TALADRO : %i",ct);printf("\nCANTIDAD DE CLIENTES QUE HICIERON LA COMPRA DE UNA HIDROLAVADORA : %i",ch);printf("\nTOTAL FACTURADO EN LA FERRETERIA : %.2f",totalfacturado);printf("\nTOTAL FACTURADO POR EL ARTICULO TALADRO : %.2f\n\n",recaudadotaladro);system("pause\n");system("cls");}
